SELECT d.id, d.bill_id, d.spd_code, d.purchase_company_id, d.purchase_company_code, d.purchase_company_name, d.purchase_company_erp_code, d.apply_org_id, d.apply_org_code, d.apply_org_name, d.apply_org_scm_code, d.apply_org_erp_code, d.recieve_org_id, d.recieve_org_code, d.recieve_org_name, d.recieve_org_scm_code, d.recieve_org_erp_code, d.prov_id, d.prov_code, d.prov_name, d.prov_erp_code, d.fillter, d.remark, d.sum_row, d. STATUS, d.fill_date, d.version, u.ename AS fillterName, d.pur_type FROM distr_bill d JOIN prov_dept_user_relation rel on rel.prov_id = d.prov_id AND rel.hos_id = #{hosId} AND rel.user_id = #{userId} LEFT JOIN sys_user_org u ON u.user_id = d.fillter
(d.is_converted =1 or d.sub_prov_id =d.prov_id)
and d.bill_id = #{billId}
and d.prov_id = #{provId}
and d.sub_prov_id = #{subProvId}
and d.is_converted = #{isConverted}
and d.purchase_company_id = #{purchaseCompanyId}
and d.recieve_org_id = #{recieveOrgId}
and d.apply_org_id = #{applyOrgId}
and d.purchase_company_name LIKE #{purchaseCompanyName}
and d.apply_org_name LIKE #{applyOrgName}
and d.recieve_org_name LIKE #{recieveOrgName}
and d.status = #{status}
and d.pur_type = #{purType}
and d.status in
#{item}
and d.purchase_company_id in
#{item}
order by d.id desc

SELECT DISTINCT d.id, d.bill_id, d.spd_code, d.purchase_company_id, d.purchase_company_code, d.purchase_company_name, d.purchase_company_erp_code, d.apply_org_id, d.apply_org_code, d.apply_org_name, d.apply_org_scm_code, d.apply_org_erp_code, d.recieve_org_id, d.recieve_org_code, d.recieve_org_name, d.recieve_org_scm_code, d.recieve_org_erp_code, d.prov_id, d.prov_code, d.prov_name, d.prov_erp_code, d.fillter, d.remark, d.sum_row, d. STATUS, d.fill_date, d.version, u.ename AS fillterName, d.pur_type FROM distr_bill d
LEFT JOIN sys_user_org u ON u.user_id = d.fillter LEFT JOIN out_stock_batch ob on ob.goods_batch_id = d.id
(d.is_converted =1 or d.sub_prov_id =d.prov_id)
and d.bill_id = #{billId}
and ob.bill_id = #{outStockBillId}
and d.prov_id = #{provId}
and d.sub_prov_id = #{subProvId}
and d.is_converted = #{isConverted}
and d.purchase_company_id = #{purchaseCompanyId}
and d.recieve_org_id = #{recieveOrgId}
and d.apply_org_id = #{applyOrgId}
and d.purchase_company_name LIKE #{purchaseCompanyName}
and d.apply_org_name LIKE #{applyOrgName}
and d.recieve_org_name LIKE #{recieveOrgName}
and d.status = #{status}
and d.pur_type = #{purType}
and d.status in
#{item}
and d.purchase_company_id in
#{item}
order by d.id desc
