mapper.select:
SELECT pl.id, pl.pid, pl.bill_id AS billId, pl.row_number AS rowNumber, pl.apply_dept_id AS applyDeptId, pl.apply_bill_id AS applyBillId, pl.apply_row_number AS applyRowNum, pl.hos_goods_id AS hosGoodsId, pl.hos_goods_code AS hosGoodsCode, pl.hos_goods_name AS hosGoodsName, pl.hos_price AS hosPrice, pl.hos_unit AS hosUnit, pl.hos_scale AS hosScale, pl.packet_code as packetCode, pl.hos_apply_qty AS hosApplyQty, pl.apply_qty AS applyQty, pl.hos_pur_qty AS hosPurQty, pl.pur_qty AS purQty, pl.unit_qty AS unitQty, pl.pur_tax AS purTax, pl.pur_tax_price AS purTaxPrice, pl.pur_amount AS purAmount, pl.pur_tax_money AS purTaxAmount, pl.qty, pl.status, pl.prov_id AS provId, pl.apply_date AS applyDate, pl.receive_date AS receiveDate, pl.version, pl.SALE_MAN AS saleMan, pl.unique_kind AS uniqueKind, o.cname AS applyDeptName, o.code AS applyDeptCode, vg.prov_goodsid AS provGoodsId, vg.goods_gg AS goodsGg, vg.made AS made, vg.mfrs_id AS mfrsId, vg.mfrs_name AS mfrsName, vg.sale_price AS salePrice, vg.goods_name AS goodsName, vg.erp_code AS erpCode, vg.unit as unit, if(vg.prov_goodsid!='',1,0) as checkMark FROM purchase_list pl LEFT JOIN sys_org o ON pl.apply_dept_id = o.id LEFT JOIN purchase p on pl.pid = p.id LEFT JOIN v_pro_hos_goodsinfo vg ON vg.id = pl.hos_goods_id and vg.prov_id = p.sub_prov_id and vg.hos_id = p.purchase_company_id
1=1 and 2=2
and pl.bill_id =#{billId}
and pl.pid =#{pid}
